/*
## MyToDoReact version 1.0.
##
## Copyright (c) 2022 Oracle, Inc.
## Licensed under the Universal Permissive License v 1.0 as shown at https://oss.oracle.com/licenses/upl/
*/
/*
 * This is the application main React component. We're using "function"
 * components in this application. No "class" components should be used for
 * consistency.
 * @author  jean.de.lavarene@oracle.com
 */
import React, { useState, useEffect } from "react";
import NewItem from "../utils/NewItem";
import API_LIST from "../utils/API";
import DeleteIcon from "@mui/icons-material/Delete";
import { Button, TableBody, CircularProgress } from "@mui/material";
import Moment from "react-moment";

/* In this application we're using Function Components with the State Hooks
 * to manage the states. See the doc: https://reactjs.org/docs/hooks-state.html
 * This App component represents the entire app. It renders a NewItem component
 * and two tables: one that lists the todo items that are to be done and another
 * one with the items that are already done.
 */
function MainPage() {
  // isLoading is true while waiting for the backend to return the list
  // of items. We use this state to display a spinning circle:
  const [isLoading, setLoading] = useState(false);
  // Similar to isLoading, isInserting is true while waiting for the backend
  // to insert a new item:
  const [isInserting, setInserting] = useState(false);
  // The list of todo items is stored in this state. It includes the "done"
  // "not-done" items:
  const [items, setItems] = useState([]);
  // In case of an error during the API call:
  const [error, setError] = useState();

  const [token, setToken] = useState(localStorage.getItem("token"));

  useEffect(() => {
    setToken(localStorage.getItem("token"));
  }, []);

  function deleteItem(deleteId) {
    // console.log("deleteItem("+deleteId+")")
    fetch(API_LIST + "/" + deleteId, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`, // ⬅️ Enviar el token en la cabecera
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        // console.log("response=");
        // console.log(response);
        if (response.ok) {
          // console.log("deleteItem FETCH call is ok");
          return response;
        } else {
          throw new Error("Something went wrong ...");
        }
      })
      .then(
        (result) => {
          const remainingItems = items.filter((item) => item.id !== deleteId);
          setItems(remainingItems);
        },
        (error) => {
          setError(error);
        }
      );
  }
  function toggleDone(event, id, description, done) {
    event.preventDefault();
    modifyItem(id, description, done).then(
      (result) => {
        reloadOneIteam(id);
      },
      (error) => {
        setError(error);
      }
    );
  }
  function reloadOneIteam(id) {
    fetch(API_LIST + "/" + id, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${token}`, // ⬅️ Enviar el token en la cabecera
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error("Something went wrong ...");
        }
      })
      .then(
        (result) => {
          const items2 = items.map((x) =>
            x.id === id
              ? {
                  ...x,
                  description: result.description,
                  done: result.done,
                }
              : x
          );
          setItems(items2);
        },
        (error) => {
          setError(error);
        }
      );
  }
  function modifyItem(id, description, done) {
    // console.log("deleteItem("+deleteId+")")
    var data = { description: description, done: done };
    return fetch(API_LIST + "/" + id, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${token}`, // ⬅️ Enviar el token en la cabecera
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    }).then((response) => {
      // console.log("response=");
      // console.log(response);
      if (response.ok) {
        // console.log("deleteItem FETCH call is ok");
        return response;
      } else {
        throw new Error("Something went wrong ...");
      }
    });
  }
  /*
    To simulate slow network, call sleep before making API calls.
    const sleep = (milliseconds) => {
      return new Promise(resolve => setTimeout(resolve, milliseconds))
    }
    */
  useEffect(
    () => {
      setLoading(true);
      console.log("token front:", token);
      fetch(API_LIST, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      })
        .then((response) => {
          if (response.ok) {
            return response.json();
          } else {
            throw new Error("Something went wrong ...");
          }
        })
        .then(
          (result) => {
            setLoading(false);
            setItems(result);
            console.log("result", result);
          },
          (error) => {
            setLoading(false);
            setError(error);
          }
        );

      //})
    },
    // https://en.reactjs.org/docs/faq-ajax.html
    [token] // empty deps array [] means
    // this useEffect will run once
    // similar to componentDidMount()
  );
  function addItem(text) {
    console.log("addItem(" + text + ")");
    setInserting(true);
    var data = {};
    console.log(data);
    data.description = text;
    fetch(API_LIST, {
      method: "POST",
      // We convert the React state to JSON and send it as the POST body
      headers: {
        Authorization: `Bearer ${token}`, // ⬅️ Enviar el token en la cabecera
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => {
        // This API doens't return a JSON document
        console.log(response);
        console.log();
        console.log(response.headers.location);
        // return response.json();
        if (response.ok) {
          return response;
        } else {
          throw new Error("Something went wrong ...");
        }
      })
      .then(
        (result) => {
          var id = result.headers.get("location");
          var newItem = { id: id, description: text };
          setItems([newItem, ...items]);
          setInserting(false);
        },
        (error) => {
          setInserting(false);
          setError(error);
        }
      );
  }
  return (
    <div className="App">
      <h1>MY TODO LIST</h1>
      <NewItem addItem={addItem} isInserting={isInserting} />
      {error && <p>Error: {error.message}</p>}
      {isLoading && <CircularProgress />}
      {!isLoading && (
        <div id="maincontent">
          <table id="itemlistNotDone" className="itemlist">
            <TableBody>
              {items &&
                items.map(
                  (item) =>
                    item.done === "done" && (
                      <tr key={item.id}>
                        <td className="description">{item.description}</td>
                        {/*<td>{JSON.stringify(item, null, 2) }</td>*/}
                        <td className="date">
                          <Moment format="MMM Do hh:mm:ss">
                            {item.creation_at}
                          </Moment>
                        </td>
                        <td>
                          <Button
                            variant="contained"
                            className="DoneButton"
                            onClick={(event) =>
                              toggleDone(
                                event,
                                item.id,
                                item.description,
                                item.status == "done"
                              )
                            }
                            size="small"
                          >
                            Done
                          </Button>
                        </td>
                      </tr>
                    )
                )}
            </TableBody>
          </table>
          <h2 id="donelist">Done items</h2>
          <table id="itemlistDone" className="itemlist">
            <TableBody>
              {items.map(
                (item) =>
                  item.status == "done" && (
                    <tr key={item.id}>
                      <td className="description">{item.description}</td>
                      <td className="date">
                        <Moment format="MMM Do hh:mm:ss">
                          {item.creation_ts}
                        </Moment>
                      </td>
                      <td>
                        <Button
                          variant="contained"
                          className="DoneButton"
                          onClick={(event) =>
                            toggleDone(
                              event,
                              item.id,
                              item.description,
                              item.status == "done"
                            )
                          }
                          size="small"
                        >
                          Undo
                        </Button>
                      </td>
                      <td>
                        <Button
                          startIcon={<DeleteIcon />}
                          variant="contained"
                          className="DeleteButton"
                          onClick={() => deleteItem(item.id)}
                          size="small"
                        >
                          Delete
                        </Button>
                      </td>
                    </tr>
                  )
              )}
            </TableBody>
          </table>
        </div>
      )}
    </div>
  );
}
export default MainPage;
